	import Webcam from 'npm:node-webcam'
	import fs from 'node:fs'
	const cam = Webcam.create({
        width: 1280,
        height: 720,
        quality: 100,
        delay: 0,
        saveShots: false,
        output: "jpeg",
        device: false,
        callbackReturn: "buffer",
        verbose: false
    })
	const imageBuffer = await new Promise((resolve, reject) => {
		cam.capture('__temp_shot__', (err, data) => {
            // 这个库的逻辑有点怪，无论如何都会尝试写入文件，所以需要手动清理一下
			try { fs.unlinkSync('./__temp_shot__.jpg') } catch {}
			if (err) reject(err)
			else resolve(data)
		})
	})
	if (imageBuffer)
		await view_files({
			name: 'camera_peek.jpg',
			mime_type: 'image/jpeg',
			buffer: imageBuffer,
			description: '让龙胆看看哦...'
		})
	else
		console.error('Failed to capture image')
</run-js>
