龙胆: <run-js>
import robot from 'npm:robotjs';
import sharp from 'npm:sharp';

const screenSize = robot.getScreenSize();
const img = robot.screen.capture(0, 0, screenSize.width, screenSize.height);

// The robotjs buffer is in BGRA format. Sharp can handle this.
const imageBuffer = await sharp(img.image, {
    raw: {
        width: img.width,
        height: img.height,
        channels: 4
    }
})
.jpeg()
.toBuffer();

await view_files({
    name: 'screen_capture.jpg',
    mime_type: 'image/jpeg',
    buffer: imageBuffer,
    description: '龙胆悄悄看一眼哦...'
});
</run-js>
